/**
 * Biblioteca centralizada de ícones SVG reutilizáveis
 * Todos os ícones são gerados como SVG com linhas (stroke) em vez de preenchimento
 * A cor é controlada via `currentColor` para permitir fácil customização via CSS
 */

export interface IconOptions {
  size?: number;
  color?: string;
  strokeWidth?: number;
}

/**
 * Gera um SVG com as opções especificadas
 */
function createSVG(paths: string[], options: IconOptions = {}): string {
  const size = options.size || 20;
  const strokeWidth = options.strokeWidth || 2;
  const color = options.color || 'currentColor';
  
  const pathsHTML = paths.map(path => 
    `<path d="${path}" stroke="${color}" stroke-width="${strokeWidth}" stroke-linecap="round" stroke-linejoin="round" fill="none"></path>`
  ).join('');
  
  return `<svg width="${size}" height="${size}" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">${pathsHTML}</svg>`;
}

