/**
 * Exporta todos os ícones em um objeto único para facilitar o acesso
 */
export const Icons = {
  ...NavigationIcons,
  ...ActionIcons,
  ...MessageIcons,
  ...StatusIcons,
};

/**
 * Helper para obter ícone por nome (string)
 */
export function getIcon(name: string, options?: IconOptions): string {
  const iconFunction = (Icons as any)[name];
  if (typeof iconFunction === 'function') {
    return iconFunction(options);
  }
  console.warn(`Ícone "${name}" não encontrado na biblioteca de ícones`);
  return '';
}

